import React from 'react';
import Footer from '../components/Layout/Footer';
import Tweet from '../components/Tweet';
import './MyPage.scss';
import dummyTweets from '../static/dummyData';


const MyPage = () => {
  const filteredTweets = dummyTweets.filter(tweet => {
    return tweet.username === "parkhacker"
  })
  // TODO : 주어진 트윗 목록(dummyTweets)중 현재 유져인 parkhacker의 트윗만 보여줘야 합니다.
  console.log(filteredTweets)

  return (
    <section className="myInfo">
      <div className="myInfo__container">
        <div className="myInfo__wrapper">
          <div className="myInfo__profile">
            {/* <img src={filteredTweets[0].picture} /> */}
          </div>
          <div className="myInfo__detail">
            <p className="myInfo__detailName">
              {/* {dummyTweets.username} */}
            </p>
            <p>28 팔로워 100 팔로잉</p>
          </div>
        </div>
      </div>
      <ul className="tweets__mypage">
        {/* TODO : 주어진 트윗 목록(dummyTweets)중 현재 유져인 parkhacker의 트윗만 보여줘야 합니다. */}
        <Tweet tweet={filteredTweets}/>
        {
          dummyTweets.filter((el,i) => {
            el.username === "parkhacker"
            return <Tweet key={el.id} tweet={el}/>
          })
        }


      </ul>
      <Footer />
    </section>
  );
};

export default MyPage;
