// TODO : useState를 react로 부터 import 합니다.
import React, { useRef, useState } from 'react';
import Footer from '../components/Layout/Footer';
import Tweet from '../components/Tweet';
import './Tweets.scss';
import dummyTweets from '../static/dummyData';

const getRandomNumber = (min, max) => {
  return parseInt(Math.random() * (Number(max) - Number(min) + 2));
};

const Tweets = () => {
  // TODO : 새로 트윗을 작성하고 전송할 수 있게 useState를 적절히 활용하세요.
  const [tweets, setTweets] = useState(dummyTweets)
  const [form, setForm] = useState({user: "parkhacker", msg: ""})

  const parsedDate = new Date().toLocaleDateString('ko-kr');
  const sortedtweets = tweets.sort((a, b) => (b.id - a.id))

  const nextId = useRef(6)
  const handleButtonClick = (event) => {
    // TODO : Tweet button 엘리먼트 클릭시 작동하는 함수를 완성하세요.
    // 트윗 전송이 가능하게 작성해야 합니다.
    const tweet = {
      id: nextId.current,
      username: form.user,
      picture: form.user === "parkhacker" ? 
      `https://randomuser.me/api/portraits/men/98.jpg` 
       :`https://randomuser.me/api/portraits/women/${getRandomNumber(1,98)}.jpg`,
      content: form.msg,
      createdAt: new Date(),
      updatedAt: new Date(),
    }
    nextId.current += 1
    setTweets((prev)=>([...prev, tweet]))
    setForm({user: "parkhacker", msg: ""})
  };

  const handleChangeInput = (event) => {
    // TODO : Tweet input 엘리먼트에 입력 시 작동하는 함수를 완성하세요.
    const {name, value} = event.currentTarget
    setForm({...form, [name]: value})
  };

  return (
      <React.Fragment>
      <div className="tweetForm__container">
        <div className="tweetForm__wrapper">
          <div className="tweetForm__profile">
            <img src="https://randomuser.me/api/portraits/men/98.jpg" />
          </div>
          <div className="tweetForm__inputContainer">
            <div className="tweetForm__inputWrapper">
              <div className="tweetForm__input">
                <input
                  type="text"
                  // defaultValue="parkhacker"
                  placeholder="your username here.."
                  name='user'
                  value={form.user}
                  className="tweetForm__input--username"
                  onChange={handleChangeInput}
                ></input>
                {/* TODO : 트윗을 작성할 수 있는 textarea 엘리먼트를 작성하세요. */}
                <textarea
                  type="text"
                  placeholder='your tweet here..'
                  name='msg'
                  value={form.msg}
                  className='tweetForm__input--message'
                  onChange={handleChangeInput}
                  ></textarea>
              </div>
              <div className="tweetForm__count" role="status">
                <span className="tweetForm__count__text">
                  {/* TODO : 트윗 총 개수를 보여줄 수 있는 Counter를 작성하세요. */}
                  total: {tweets.length}
                </span>
              </div>
            </div>
            <div className="tweetForm__submit">
              <div className="tweetForm__submitIcon"></div>
              {/* TODO : 작성한 트윗을 전송할 수 있는 button 엘리먼트를 작성하세요. */}
              <button 
                type='button' 
                className="tweetForm__submitButton"
                onClick={handleButtonClick}
              >Tweet</button>
            </div>
          </div>
        </div>
      </div>
      <div className="tweet__selectUser"></div>
      <ul className="tweets">
        {/* TODO : 하나의 트윗이 아니라, 주어진 트윗 목록(dummyTweets) 갯수에 맞게 보여줘야 합니다. */}  
        {/* MEMO: 1) tweet={dummyTweets[i]}로 하면 createdAt이 undefined라고 에러발생하는 이유가 뭔가요? 
                     tweets가 갱신한 값이라 tweets로 해야하는 건가요..?
                  2) map돌 때 el값만 주고 key={el.id} tweets[el]하면 위와 같은 에러가 발생하는데 이유가 뭔지 모르겠습니다..
                     props로 전해줘야할땐 꼭 index값으로 전달해줘야 하는건가요?
        */}
        {
          sortedtweets.map((el,i)=>{
            return (
              <Tweet key={el.id} tweet={tweets[i]} />
            )
          })
        }
      </ul>
      <Footer />
    </React.Fragment>
  );
};

export default Tweets;
