// TODO : useStateÎ•º reactÎ°ú Î∂ÄÌÑ∞ import Ìï©ÎãàÎã§.
import React, { useEffect, useState } from 'react';
import Footer from '../../components/Layout/Footer';
import Tweet from '../../components/Tweet';
import styles from "./Tweets.module.css";
import dummyTweets from '../../static/dummyData';

const getRandomNumber = (min, max) => {
  return parseInt(Math.random() * (Number(max) - Number(min) + 2));
};

const Tweets = () => {
  // TODO : ÏÉàÎ°ú Ìä∏ÏúóÏùÑ ÏûëÏÑ±ÌïòÍ≥† Ï†ÑÏÜ°Ìï† Ïàò ÏûàÍ≤å useStateÎ•º Ï†ÅÏ†àÌûà ÌôúÏö©ÌïòÏÑ∏Ïöî.
  //üí°MEMO: ÏÉàÎ°úÍ≥†Ïπ®ÏãúÏóêÎèÑ tweetÏ∂îÍ∞ÄÌïú Í∞íÏùÑ Ïú†ÏßÄÌïòÍ≥† Ïã∂Ïñ¥ÏÑú ÏΩîÎìúÎ•º ÏßúÎ¥§ÎäîÎç∞ Ïù¥Í≤ÉÏ†ÄÍ≤É ÏÑûÏó¨ÏÑú Î≥µÏû°Ìï¥Î≥¥Ïù¥ÎäîÍ±∞ Í∞ôÏïÑÏöî.. Îçî Ï¢ãÏùÄ Î∞©Î≤ïÏûàÏùÑÍπåÏöî?
  const [tweets, setTweets] = useState(
    localStorage.getItem(dummyTweets) 
    ? JSON.parse(localStorage.getItem(dummyTweets))
    : dummyTweets);

  useEffect(() => {
    const tweets_local = localStorage.getItem(dummyTweets);
    if (tweets_local) {
      setTweets(JSON.parse(tweets_local));
    }
  }, []);

  useEffect(() => {
    if (tweets) {
      localStorage.setItem(dummyTweets, JSON.stringify(tweets));
    }
  }, [tweets]);

  const [form, setForm] = useState({user: "parkhacker", msg: ""});

  const sortedtweets = tweets.sort((a, b) => (b.id - a.id));

  const handleButtonClick = (event) => {
    // TODO : Tweet button ÏóòÎ¶¨Î®ºÌä∏ ÌÅ¥Î¶≠Ïãú ÏûëÎèôÌïòÎäî Ìï®ÏàòÎ•º ÏôÑÏÑ±ÌïòÏÑ∏Ïöî.
    // Ìä∏Ïúó Ï†ÑÏÜ°Ïù¥ Í∞ÄÎä•ÌïòÍ≤å ÏûëÏÑ±Ìï¥Ïïº Ìï©ÎãàÎã§.
    const tweet = {
      id: tweets.length + 1,
      username: form.user,
      picture: form.user === "parkhacker" ? 
      `https://randomuser.me/api/portraits/men/98.jpg` 
       :`https://randomuser.me/api/portraits/women/${getRandomNumber(1,98)}.jpg`,
      content: form.msg,
      createdAt: new Date(),
      updatedAt: new Date(),
    }
    setTweets((prev)=>([...prev, tweet]));
    setForm({user: "parkhacker", msg: ""});
  };

  const handleChangeInput = (event) => {
    // TODO : Tweet input ÏóòÎ¶¨Î®ºÌä∏Ïóê ÏûÖÎ†• Ïãú ÏûëÎèôÌïòÎäî Ìï®ÏàòÎ•º ÏôÑÏÑ±ÌïòÏÑ∏Ïöî.
    const {name, value} = event.currentTarget;
    setForm({...form, [name]: value});
  };

  return (
      <React.Fragment>
      <div className={styles.tweetForm__container}>
        <div className={styles.tweetForm__wrapper}>
          <div className={styles.tweetForm__profile}>
            <img src="https://randomuser.me/api/portraits/men/98.jpg" />
          </div>
          <div className={styles.tweetForm__inputContainer}>
            <div className={styles.tweetForm__inputWrapper}>
              <div className={styles.tweetForm__input}>
                <input
                  type="text"
                  placeholder="your username here.."
                  name="user"
                  value={form.user}
                  className={styles["tweetForm__input--username"]}
                  onChange={handleChangeInput}
                ></input>
                {/* TODO : Ìä∏ÏúóÏùÑ ÏûëÏÑ±Ìï† Ïàò ÏûàÎäî textarea ÏóòÎ¶¨Î®ºÌä∏Î•º ÏûëÏÑ±ÌïòÏÑ∏Ïöî. */}
                <textarea
                  type="text"
                  placeholder='your tweet here..'
                  name="msg"
                  value={form.msg}
                  className={styles["tweetForm__input--message"]}
                  onChange={handleChangeInput}
                  ></textarea>
              </div>
              <div className={styles.tweetForm__count} role="status">
                <span className={styles.tweetForm__count__text}>
                  {/* TODO : Ìä∏Ïúó Ï¥ù Í∞úÏàòÎ•º Î≥¥Ïó¨Ï§Ñ Ïàò ÏûàÎäî CounterÎ•º ÏûëÏÑ±ÌïòÏÑ∏Ïöî. */}
                  total: {tweets.length}
                </span>
              </div>
            </div>
            <div className={styles.tweetForm__submit}>
              <div className={styles.tweetForm__submitIcon}></div>
              {/* TODO : ÏûëÏÑ±Ìïú Ìä∏ÏúóÏùÑ Ï†ÑÏÜ°Ìï† Ïàò ÏûàÎäî button ÏóòÎ¶¨Î®ºÌä∏Î•º ÏûëÏÑ±ÌïòÏÑ∏Ïöî. */}
              <button 
                type="button"
                className={styles.tweetForm__submitButton}
                onClick={handleButtonClick}
              >Tweet</button>
            </div>
          </div>
        </div>
      </div>
      <div className={styles.tweet__selectUser}></div>
      <ul className={styles.tweets}>
        {/* TODO : ÌïòÎÇòÏùò Ìä∏ÏúóÏù¥ ÏïÑÎãàÎùº, Ï£ºÏñ¥ÏßÑ Ìä∏Ïúó Î™©Î°ù(dummyTweets) Í∞ØÏàòÏóê ÎßûÍ≤å Î≥¥Ïó¨Ï§òÏïº Ìï©ÎãàÎã§. */}  
        {
          sortedtweets.map((tweet)=>{
            return <Tweet key={tweet.id} tweet={tweet} />
          })
        }
      </ul>
      <Footer />
    </React.Fragment>
  );
};

export default Tweets;
